import React from 'react';
import ReactDOM from 'react-dom';
import './style.css';
import RobotInterface from './RobotInterface';

ReactDOM.render(<RobotInterface />, document.getElementById('appbody'));

var ws
function connect_to_server(data){
	if ("WebSocket" in window) {
		// open web socket
		ws = new WebSocket("ws://192.168.0.125:8765");      
		ws.onopen = function() {
			send_data_to_server(data);
		};          
	} else {
		// browser doesn't support WebSocket
	}        
}

function send_data_to_server(data){
	ws.send(data);
}

const keyDownFunction = function(event) {
	var keycode = (event.keyCode ? event.keyCode : event.which);
	console.log(keycode);
	/*up*/
	if (keycode == '38'){
		connect_to_server("The robot should move forward");
		console.log("forward");
	}

	/*right*/
	if (keycode == '39'){
		connect_to_server("The robot should move right");
	}

	/*down*/
	if (keycode == '40'){
		connect_to_server("The robot should move backwards");
	}

	/*left*/
	if (keycode == '37'){ 
		connect_to_server("The robot should move left");
	}

	/*stop - spacebar*/
	if (keycode == '32'){
		connect_to_server("Stop");
	}
}

const keyUpFunction = function(event) {
	var keycode = (event.keyCode ? event.keyCode : event.which);
	console.log(keycode);
	/*up*/
	if (keycode == '38'){
		connect_to_server("Stop");
		console.log("Stop");
	}

	/*right*/
	if (keycode == '39'){
		connect_to_server("Stop");
	}

	/*down*/
	if (keycode == '40'){
		connect_to_server("Stop");
	}

	/*left*/
	if (keycode == '37'){ 
		connect_to_server("Stop");
	}

	/*stop - spacebar*/
	if (keycode == '32'){
		connect_to_server("Stop");
	}
}

document.addEventListener("keydown", keyDownFunction, false);
document.addEventListener("keyup", keyUpFunction, false);
